import React, { Component } from "react";

class Tab extends Component {
  render() {
    const {items, activeTab} = this.props;
    return (
      <React.Fragment>
        <ul className="tabs">
          {items.map((item) => (
            <li className={[item.active ? "active" : "inactive"]} key={item.id} onClick={activateTab}>
              {item.name}
            </li>
          ))}
        </ul>
        <div className="contents">
          {items.map((item) => (
            <section
              className={[item.active ? "active" : "inactive"]}
              key={item.id}
            >
              test {item.id}
            </section>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Tab;
