import React, { Component } from "react";
import BuyLists from "./buylists";
import Fridge from "./fridge";

class Tab extends Component {
  activeTab = (item) => {
    items.map((item) => {
      item.active = item.id === itemId;
    });
  };

  render() {
    const items = [
      { id: 1, name: "買い物", active: true, comp: BuyLists },
      { id: 2, name: "冷蔵庫", active: false, comp: Fridge },
    ];
    return (
      <React.Fragment>
        <ul className="tabs">
          {items.map((item) => (
            <li
              onClick={() => this.activeTab(item)}
              className={[item.active ? "active" : "inactive"]}
              key={item.id}
            >
              {item.name}
            </li>
          ))}
        </ul>
        <div className="contents">
          {items.map((item) => (
            <section
              className={[item.active ? "active" : "inactive"]}
              key={item.id}
            >
              <item.comp />
            </section>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Tab;
