import React, { Component } from "react";

class Tab extends Component {
  render() {
    const items = this.props.items;
    return (
      <React.Fragment>
        <ul className="tabs">
          {items.map((item) => (
            <li className={[item.active? 'active': 'inactive']} key={item.id}>{item.name}</li>
          ))}
        </ul>
        <div className="contents">
          {items.map((item) => (
            <section className={[item.active? 'active': 'inactive']} key={item.id} >test</section>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Tab;
