import React, { Component } from "react";
import Tab from "./components/tab";
import BuyLists from "./components/buylists";
import Fridge from "./components/fridge";
import ReactDOM from 'react-dom';
import { library } from '@fortawesome/fontawesome-svg-core'; //fontawesomeのコアファイル
import { fab } from '@fortawesome/free-brands-svg-icons'; //fontawesomeのbrandアイコンのインポート
import { fas } from '@fortawesome/free-solid-svg-icons'; //fontawesomeのsolidアイコンのインポート
import { far } from '@fortawesome/free-regular-svg-icons'; //fontawesomeのregularアイコンのインポート

library.add(fab, fas, far); //他のコンポーネントから簡単に呼び出せるようにするための登録処理？

class App extends Component {
  state = {
    items: [
      { id: 1, name: "買い物", active: true, comp: BuyLists},
      { id: 2, name: "冷蔵庫", active: false, comp: Fridge},
    ],
  };
  activeTab = (itemId) => {
    const items = [...this.state.items];
    items.map(item => {
      item.active = item.id === itemId
    });
    this.setState({ items });
  };

  render() {
    return (
      <div className="container">
        <Tab items={this.state.items} activeTab={this.activeTab}></Tab>
      </div>
    );
  }
}

export default App;
